import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

function App() {
  let post = "Jordan 3";
  let [titles, setTitles] = useState([
    "ÎÇ®ÏûêÏΩîÌä∏ Ï∂îÏ≤ú",
    "Í∞ïÎÇ® Ïö∞ÎèôÎßõÏßë",
    "ÌååÏù¥Ïç¨ ÎèÖÌïô",
  ]);
  let [likes, setLikes] = useState([0, 0, 0]);

  let [modals, setModals] = useState([false, false, false]);
  let [userInput, setUserInput] = useState("");

  return (
    <div className="App">
      <div className="black-nav">
        <h4>GirlsDo</h4>
      </div>
      <button
        onClick={() => {
          let copy = [...titles];
          copy = copy.sort();
          setTitles(copy);
        }}
      >
        Alphabetical Order
      </button>

      {titles.map(function (title, i) {
        return (
          <div className="list" key={i}>
            <h4
              onClick={() => {
                let copy = [...modals];
                copy[i] = !copy[i];
                setModals(copy);
              }}
            >
              {title}{" "}
              <span
                onClick={() => {
                  let copy = [...likes];
                  copy[i] += 1;
                  setLikes(copy);
                }}
              >
                üëç
              </span>{" "}
              {likes[i]}
            </h4>
            <p>2nd February Published</p>
            {modals[i] == true ? (
              <Modal titles={titles} i={i} setTitles={setTitles} />
            ) : null}
          </div>
        );
      })}
      <div>
        {" "}
        <input
          id="newPost"
          onChange={(e) => {
            setUserInput(e.target.value);
          }}
        />
        <button
          onClick={() => {
            let copy1 = [...titles];
            let copy2 = [...likes];
            copy1 = [...titles, userInput];
            copy2 = [...likes, 0];
            setTitles(copy1);
            setLikes(copy2);
            setUserInput("");
          }}
        >
          Submit
        </button>
      </div>
    </div>
  );
}

function Modal(props) {
  return (
    <div className="modal">
      <h4>{props.titles[props.i]}</h4>
      <p>Date</p>
      <p>Details</p>
      <button
        onClick={() => {
          let copy = [...props.titles];
          copy[0] = "Ïó¨ÏûêÏΩîÌä∏ Ï∂îÏ≤ú";
          props.setTitles(copy);
        }}
      >
        Edit First Title
      </button>
    </div>
  );
}

export default App;
