import React from "react";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Modal from "react-modal";
import {
  faCartShopping,
  faMagnifyingGlass,
  faUser,
  faBarsStaggered,
  faBagShopping,
} from "@fortawesome/free-solid-svg-icons";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };
  const modalStyle = {
    overlay: {
      backgroundColor: "rgba(0, 0, 0, 0.5)",
      display: "flex",
      alignItems: "center",
      justifyContent: "flex-end",
    },
    content: {
      width: "50%",
      height: "100%",
      margin: "auto",
      backgroundColor: "#fff",
      border: "none",
      right: 0,
      position: "absolute",
      left: "auto",
      display: "flex",
      flexDirection: "column", // 수직 방향으로 요소 배치
    },
  };

  return (
    <div className="black-nav">
      <div className="left">
        <FontAwesomeIcon icon={faUser} className="icons" />
      </div>
      <h1 className="center">
        <Link to="/">
          <img
            src="https://i.imgur.com/F8FF2Zk.png"
            width={40}
            height={60}
            className="logo"
          ></img>
        </Link>
      </h1>
      <div className="right">
        <FontAwesomeIcon icon={faMagnifyingGlass} className="icons" />
        <Link to="/cart">
          <FontAwesomeIcon icon={faCartShopping} className="icons" />
        </Link>
        <FontAwesomeIcon
          icon={faBarsStaggered}
          className="icons"
          onClick={() => setModalIsOpen(!modalIsOpen)}
        />
        <Modal
          isOpen={isOpen}
          onRequestClose={closeModal}
          style={modalStyle}
          contentLabel="Example Modal"
        >
          <div style={modalStyle.contentInner}>
            <div>
              <h2>Hello Modal</h2>
              <p>This is an example modal.</p>
            </div>
            <button onClick={closeModal}>Close Modal</button>
          </div>
        </Modal>
      </div>
    </div>
  );
};

export default Navbar;
