import logo from "./logo.svg";
import "./App.css";
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCartShopping,
  faMagnifyingGlass,
  faUser,
  faBarsStaggered,
} from "@fortawesome/free-solid-svg-icons";
import { Routes, Route, Link } from "react-router-dom";

const HiddenDiv = () => {
  const [isHidden, setIsHidden] = useState(false);
  const [prevScrollPos, setPrevScrollPos] = useState(window.pageYOffset);

  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset;

    if (currentScrollPos === 0) {
      setIsHidden(false);
    } else if (currentScrollPos > prevScrollPos && !isHidden) {
      // Scrolling down
      setIsHidden(true);
    } else if (currentScrollPos < prevScrollPos && isHidden) {
      // Scrolling up
      setIsHidden(false);
    }

    // Update the previous scroll position
    setPrevScrollPos(currentScrollPos);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [isHidden, prevScrollPos]);

  return (
    <div className={`hidden-div ${isHidden ? "hide" : "show"}`}>
      <p>Free Shipping</p>
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <div className="black-nav">
        <div className="left">
          <FontAwesomeIcon icon={faUser} className="icons" />
        </div>
        <h1 className="center">
          <Link to="/home">
            <img
              src="https://assets.stickpng.com/images/62b1f1508309c32ce1623dbe.png"
              width={40}
              height={60}
              className="logo"
            ></img>
          </Link>
        </h1>
        <div className="right">
          <FontAwesomeIcon icon={faMagnifyingGlass} className="icons" />
          <Link to="/cart">
            <FontAwesomeIcon icon={faCartShopping} className="icons" />
          </Link>
          <FontAwesomeIcon icon={faBarsStaggered} className="icons" />
        </div>
      </div>

      <HiddenDiv />
    </div>
  );
}

export default App;
