import React, { useState, useEffect, useRef } from "react";

const CareService = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [imageTranslateY, setImageTranslateY] = useState(0);
  const prevScrollPosition = useRef(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPosition = window.scrollY;
      const scrollSpeed = currentScrollPosition - prevScrollPosition.current;
      prevScrollPosition.current = currentScrollPosition;

      // 이미지가 이동할 수 있는 최대 높이를 설정합니다.
      const maxScrollHeight = 500; // 예시로 500px로 설정합니다.

      // 이미지가 이동해야 하는 거리를 계산합니다. 스크롤 속도에 따라 조절할 수 있습니다.
      const translateY = Math.min(
        scrollPosition + scrollSpeed * 2,
        maxScrollHeight
      );

      // 이미지의 위치를 업데이트합니다.
      setImageTranslateY(translateY);
      // 스크롤 위치를 업데이트합니다.
      setScrollPosition(currentScrollPosition);
    };

    // 스크롤 이벤트를 추가합니다.
    window.addEventListener("scroll", handleScroll);

    return () => {
      // 컴포넌트가 언마운트될 때 스크롤 이벤트를 제거합니다.
      window.removeEventListener("scroll", handleScroll);
    };
  }, [scrollPosition]); // 스크롤 위치에만 의존

  return (
    <div
      className="floating-image"
      style={{ transform: `translateY(${imageTranslateY}px)` }}
    >
      {/* 이미지 컴포넌트 */}
      <img
        src="https://mohawkarmynavy.com/wp-content/uploads/2019/11/carhartt-coats.png"
        alt="Floating Image"
      />
    </div>
  );
};

export default CareService;
