import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCartShopping,
  faMagnifyingGlass,
  faUser,
  faBarsStaggered,
} from "@fortawesome/free-solid-svg-icons";
import { Routes, Route, Link } from "react-router-dom";

function App() {
  let post = "Jordan 3";
  let [titles, setTitles] = useState([
    "남자코트 추천",
    "강남 우동맛집",
    "파이썬 독학",
  ]);
  let [likes, setLikes] = useState([0, 0, 0]);

  let [modals, setModals] = useState([false, false, false]);
  let [userInput, setUserInput] = useState("");

  return (
    <div className="App">
      <div className="black-nav">
        <div className="left">
          <FontAwesomeIcon icon={faUser} className="icons" />
        </div>
        <h1 className="center">
          <img
            src="https://assets.stickpng.com/images/62b1f1508309c32ce1623dbe.png"
            width={40}
            height={60}
          ></img>
        </h1>
        <div className="right">
          <FontAwesomeIcon icon={faMagnifyingGlass} className="icons" />
          <FontAwesomeIcon icon={faCartShopping} className="icons" />
          <FontAwesomeIcon icon={faBarsStaggered} className="icons" />
        </div>
      </div>
      <button
        onClick={() => {
          let copy = [...titles];
          copy = copy.sort();
          setTitles(copy);
        }}
        className="top1"
      >
        Alphabetical Order
      </button>

      {titles.map(function (title, i) {
        return (
          <div className="list" key={i}>
            <h4
              onClick={() => {
                let copy = [...modals];
                copy[i] = !copy[i];
                setModals(copy);
              }}
            >
              {title}{" "}
              <span
                onClick={(e) => {
                  e.stopPropagation();
                  let copy = [...likes];
                  copy[i] += 1;
                  setLikes(copy);
                }}
              >
                👍
              </span>{" "}
              {likes[i]}
            </h4>
            <p>2nd February Published</p>
            {modals[i] == true ? (
              <Modal titles={titles} i={i} setTitles={setTitles} />
            ) : null}
          </div>
        );
      })}
      <div>
        {" "}
        <input
          id="newPost"
          onChange={(e) => {
            setUserInput(e.target.value);
          }}
        />
        <button
          onClick={() => {
            let copy1 = [...titles];
            let copy2 = [...likes];
            copy1 = [...titles, userInput];
            copy2 = [...likes, 0];
            setTitles(copy1);
            setLikes(copy2);
            setUserInput("");
            document.getElementById("newPost").value = "";
          }}
        >
          Submit
        </button>
      </div>
    </div>
  );
}

function Modal(props) {
  return (
    <div className="modal">
      <h4>{props.titles[props.i]}</h4>
      <p>Date</p>
      <p>Details</p>
      <button
        onClick={() => {
          let copy = [...props.titles];
          copy[0] = "여자코트 추천";
          props.setTitles(copy);
        }}
      >
        Edit First Title
      </button>
    </div>
  );
}

export default App;
