import React, { useState, useEffect } from "react";

const CareService = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [maxScrollHeight, setMaxScrollHeight] = useState(1000); // 이미지가 이동할 수 있는 최대 높이
  const [imageTranslateY, setImageTranslateY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      // 현재 스크롤 위치를 업데이트합니다.
      setScrollPosition(window.scrollY);
    };

    // 스크롤 이벤트를 추가합니다.
    window.addEventListener("scroll", handleScroll);

    return () => {
      // 컴포넌트가 언마운트될 때 스크롤 이벤트를 제거합니다.
      window.removeEventListener("scroll", handleScroll);
    };
  }, []); // 한 번만 등록하고 해제

  // 이미지의 위치를 계산합니다.
  useEffect(() => {
    // 이미지가 이동해야 하는 높이를 계산합니다.
    const translateY = Math.min(scrollPosition, maxScrollHeight);

    // 이미지의 위치를 업데이트합니다.
    setImageTranslateY(translateY);
  }, [scrollPosition, maxScrollHeight]);

  return (
    <div
      className="floating-image"
      style={{
        transform: `translateY(${imageTranslateY}px)`,
        marginTop: "20px", // 이미지가 위쪽 테두리로부터 일정한 거리를 두도록 marginTop을 조절합니다.
      }}
    >
      {/* 이미지 컴포넌트 */}
      <img
        src="https://mohawkarmynavy.com/wp-content/uploads/2019/11/carhartt-coats.png"
        alt="Floating Image"
      />
      <div></div>
    </div>
  );
};

export default CareService;
