import React from "react";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Modal from "react-modal";
import {
  faCartShopping,
  faMagnifyingGlass,
  faUser,
  faBarsStaggered,
  faBagShopping,
} from "@fortawesome/free-solid-svg-icons";

const Navbar = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const modalStyle = {
    overlay: {
      backgroundColor: "rgba(0, 0, 0, 0.5)",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    content: {
      width: "33%",
      height: "100%",
      margin: "auto",
      backgroundColor: "#fff",
      border: "none",
      right: 0,
      position: "absolute",
      left: "auto",
      display: "flex",
      flexDirection: "column",
      opacity: 0,
      transform: "translateY(-50px)",
      transition: "opacity 0.3s ease, transform 0.3s ease",
    },
  };

  // 모달이 열릴 때의 스타일
  const modalOpenStyle = {
    content: {
      ...modalStyle.content,
      opacity: 1,
      transform: "translateY(0)",
    },
  };

  return (
    <div className="black-nav">
      <div className="left">
        <FontAwesomeIcon icon={faUser} className="icons" />
      </div>
      <h1 className="center">
        <Link to="/">
          <img
            src="https://i.imgur.com/F8FF2Zk.png"
            width={40}
            height={60}
            className="logo"
          ></img>
        </Link>
      </h1>
      <div className="right">
        <FontAwesomeIcon icon={faMagnifyingGlass} className="icons" />
        <Link to="/cart">
          <FontAwesomeIcon icon={faCartShopping} className="icons" />
        </Link>
        <FontAwesomeIcon
          icon={faBarsStaggered}
          className="icons"
          onClick={() => setModalIsOpen(!modalIsOpen)}
        />
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={() => setModalIsOpen(false)}
          style={{ ...modalStyle, ...(modalIsOpen && modalOpenStyle) }} // 모달이 열릴 때와 닫힐 때 서로 다른 스타일 적용
        >
          <div>
            <div className="space"></div>
            <p>MEN</p>
            <p>WOMEN</p>
            <p>GRADESCHOOL</p>
            <p>DOBLUE</p>
          </div>
        </Modal>
      </div>
    </div>
  );
};

export default Navbar;
